<template>
    <label :class="wrapClasses">
        <span :class="checkboxClasses">
            <span :class="innerClasses"></span>
            <input
                v-if="group"
                v-model="model"
                type="checkbox"
                :class="inputClasses"
                :disabled="disabled"
                :value="label"
                :name="name"
                @change="handleChange">
            <input
                v-else
                type="checkbox"
                :class="inputClasses"
                :disabled="disabled"
                :checked="currentValue"
                :name="name"
                @change="handleChange">
        </span>
        <span v-if="showSlot"><slot>{{ label }}</slot></span>
    </label>
</template>
<script>
    import { findComponentUpward, oneOf } from '../../../utils/assist'
    import Emitter from '../../../mixins/emitter'

    const prefixCls = 'yy-checkbox'
    export default {
        name: 'YYCheckbox',
        mixins: [Emitter],
        props: {
            value: {
                type: [String, Number, Boolean],
                default: false
            },
            size: {
                validator(value) {
                    return oneOf(value, ['small', 'large'])
                }
            },
            disabled: {
                type: Boolean,
                default: false
            },
            trueValue: {
                type: [String, Number, Boolean],
                default: true
            },
            falseValue: {
                type: [String, Number, Boolean],
                default: false
            },
            label: {
                type: [String, Number, Boolean]
            },
            indeterminate: {
                type: Boolean,
                default: false
            },
            name: {
                type: String
            }
        },
        data() {
            return {
                model: [],
                currentValue: this.value,
                group: false,
                showSlot: true,
                parent: findComponentUpward(this, 'YYCheckboxGroup')
            }
        },
        computed: {
            wrapClasses() {
                return [
                    `${prefixCls}-wrapper`,
                    {
                        [`${prefixCls}-group-item`]: this.group,
                        [`${prefixCls}-wrapper-checked`]: this.currentValue,
                        [`${prefixCls}-wrapper-disabled`]: this.disabled,
                        [`${prefixCls}-wrapper-${this.size}`]: !!this.size
                    }
                ]
            },
            checkboxClasses() {
                return [
                    `${prefixCls}`,
                    {
                        [`${prefixCls}-checked`]: this.currentValue,
                        [`${prefixCls}-disabled`]: this.disabled,
                        [`${prefixCls}-indeterminate`]: this.indeterminate
                    }
                ]
            },
            innerClasses() {
                return `${prefixCls}-inner`
            },
            inputClasses() {
                return `${prefixCls}-input`
            }
        },
        watch: {
            value(val) {
                if (val === this.trueValue || val === this.falseValue) {
                    this.updateValue()
                }
            }
        },
        methods: {
            updateValue() {
                this.currentValue = this.value === this.trueValue
            },
            handleChange(event) {
                if (this.disabled) {
                    return false
                }

                const checked = event.target.checked
                this.currentValue = checked

                const value = checked ? this.trueValue : this.falseValue
                this.$emit('input', value)

                if (this.group) {
                    this.parent.handleChange(this.model)
                } else {
                    this.$emit('on-change', value)
                    this.dispatch('YYFormItem', 'on-form-change', value)
                }
            }
        },
        mounted() {
            this.parent = findComponentUpward(this, 'YYCheckboxGroup')
            this.group = !!this.parent

            if (this.group) {
                this.parent.updateModel(true)
            } else {
                this.updateValue()
                this.showSlot = this.$slots.default !== undefined
            }
        }
    }
</script>
